import React, { useRef, useState } from "react";

function Ast() {
  const [textValue, setTextValue] = useState("");
  let displayCode1 = useRef();
  let refTextArea = useRef();

  const handleTextAreaChange = (event) => {
    setTextValue(event.target.value);
    displayCode1.current.innerHTML = textValue;
  };

  return (
    <div>
      <textarea
        rows="20"
        cols="50"
        value={textValue}
        onChange={handleTextAreaChange}
      ></textarea>
      <button onClick={handleTextAreaChange}>AST comparison </button>
      <span ref={displayCode1}></span>
    </div>
  );
}

export default Ast;
