import React, { useRef, useState } from "react";

function Ast() {
  const [textValue, setTextValue] = useState("");
  let displayCode1 = useRef();
  let refTextArea = useRef();

  const handleTextAreaChange = (event) => {
    setTextValue(refTextArea.current.value);
    // displayCode1.current.innerHTML = textValue;
    //console.log(refTextArea.current.value);
  };

  const handleASTComparison = () => {
    console.log(textValue);
    displayCode1.current.innerHTML = textValue;
  };

  return (
    <div>
      <div className="divInputs">
        <label>Code 1 :</label>
        <input
          type="file"
          value={textValue}
          onChange={handleTextAreaChange}
          ref={refTextArea}
        ></input>
        <label>Code 2 :</label>
        <input
          type="file"
          value={textValue}
          onChange={handleTextAreaChange}
          ref={refTextArea}
        ></input>
      </div>
      <div className="divButton">
        <button onClick={handleASTComparison}>AST comparison </button>
      </div>

      <span ref={displayCode1}></span>
    </div>
  );
}

export default Ast;
