import React, { useRef, useState } from "react";

function Ast() {
  const [textValue, setTextValue] = useState("");
  let displayCode1 = useRef();
  let refTextArea = useRef();
  let [selectedFile1, setSelectedFile1] = useState(null);
  let [selectedFile2, setSelectedFile2] = useState(null);

  const handleFileChange = (event, setFile) => {
    const file = event.target.files[0];

    if (file) {
      if (file.name.endsWith(".txt")) {
        setFile((prevFile) => file);
      } else {
        alert("Please select a .txt file.");
        event.target.value = null;
      }
    }
  };

  const handleTextAreaChange1 = (event) => {
    //setTextValue(refTextArea.current.value);
    // displayCode1.current.innerHTML = textValue;
    //console.log(refTextArea.current.value);

    handleFileChange(event, selectedFile1);
  };

  const handleTextAreaChange2 = (event) => {
    handleFileChange(event, selectedFile2);
  };

  const handleASTComparison = () => {
    //console.log(textValue);
    // displayCode1.current.innerHTML = textValue;
  };

  return (
    <div>
      <div className="divInputs">
        <label>Code 1:</label>
        <input
          type="file"
          accept=".txt"
          value={textValue}
          onChange={handleTextAreaChange1}
          ref={refTextArea}
        ></input>

        <label>Code 2:</label>
        <input
          type="file"
          accept=".txt"
          value={textValue}
          onChange={handleTextAreaChange2}
          ref={refTextArea}
        ></input>
      </div>
      <div>
        <h4> {selectedFile1 && <p>Selected File 1:{selectedFile1.name}</p>}</h4>
        <h4> {selectedFile2 && <p>Selected File 2:{selectedFile1.name}</p>}</h4>
      </div>
      <div className="divButton">
        <button onClick={handleASTComparison}>AST comparison </button>
      </div>

      <span ref={displayCode1}></span>
      <div>
        <h1>Result : </h1>
      </div>
    </div>
  );
}

export default Ast;
